tinymce.PluginManager.add("lowercase", function (e) {
    e.addButton("lowercase", {
        //stext: "Lowercase",
		  image : '/content/js/tinymce/icons/lowercase.png',
		  title: "Lowercase",
        onclick: function () {
            lower(e);
        }
    }), e.addMenuItem("lowercase", {
        text: "Lowercase",
		  image : '/content/js/tinymce/icons/lowercase.png',
        context: "tools",
        onclick: function () {           
				lower(e);
        }
    })
});

function lower(e){
		var sel = e.dom.decode(e.selection.getContent());
		sel = sel.toLowerCase();
		e.selection.setContent(sel);
		e.save();
		e.isNotDirty = true;
}