tinymce.PluginManager.add("uppercase", function (e) {
    e.addButton("uppercase", {
       // text: "Uppercase",		 
		   image : '/content/js/tinymce/icons/uppercase.png',
		  title: "Uppercase",
        onclick: function () {
            upper(e);
        }
    }), e.addMenuItem("example", {
        text: "Uppercase",
	     image : '/content/js/tinymce/icons/uppercase.png',
		  title: "Uppercase",
        context: "tools",
        onclick: function () {           
				upper(e);
        }
    })
});

function upper(e){
		var sel = e.dom.decode(e.selection.getContent());
		sel = sel.toUpperCase();
		e.selection.setContent(sel);
		e.save();
		e.isNotDirty = true;
}